Rules.InvariantScript:
Set(varEntryMode, "New");
Set(varEditReading, Defaults(WQ_Readings));
Set(varDefaultTime, Text(Now(), "HH:mm"));
Reset(txtReadingTime);
Reset(dpReadingDate);

Set(varSaveError, "");

If(
    IsBlank(varMyFarm),
    Set(varSaveError, "Farm is not set. Go to Home, select a farm, then press Save settings.")
);

If(
    IsBlank(varSaveError) && IsBlank(ddSite.Selected.Site),
    Set(varSaveError, "Please select a Site.")
);

If(
    IsBlank(varSaveError) && IsBlank(ddUnit.Selected.UnitId),
    Set(varSaveError, "Please select a Unit.")
);

If(
    IsBlank(varSaveError) && IsBlank(Trim(txtInitials.Text)),
    Set(varSaveError, "Please enter initials.")
);

If(
    IsBlank(varSaveError) && IsBlank(dpReadingDate.SelectedDate),
    Set(varSaveError, "Please select a date.")
);

If(
    IsBlank(varSaveError) &&
    !(
        IsMatch(txtReadingTime.Text, "^\d\d:\d\d$") &&
        Value(Left(txtReadingTime.Text, 2)) < 24 &&
        Value(Mid(txtReadingTime.Text, 4, 2)) < 60
    ),
    Set(varSaveError, "Time must be in HH:mm (e.g., 07:30).")
);

/* Numeric validation (only when a value is provided) */
If(IsBlank(varSaveError) && !IsBlank(Trim(txtAlkalinity.Text))   && !IsNumeric(txtAlkalinity.Text),   Set(varSaveError, "Alkalinity must be a number."));
If(IsBlank(varSaveError) && !IsBlank(Trim(txtGHardness.Text))    && !IsNumeric(txtGHardness.Text),    Set(varSaveError, "G HARD must be a number."));
If(IsBlank(varSaveError) && !IsBlank(Trim(txtpH.Text))           && !IsNumeric(txtpH.Text),           Set(varSaveError, "pH must be a number."));
If(IsBlank(varSaveError) && !IsBlank(Trim(txtPhosphorus.Text))   && !IsNumeric(txtPhosphorus.Text),   Set(varSaveError, "PHOS must be a number."));
If(IsBlank(varSaveError) && !IsBlank(Trim(txtTAN.Text))          && !IsNumeric(txtTAN.Text),          Set(varSaveError, "AMMO (TAN) must be a number."));
If(IsBlank(varSaveError) && !IsBlank(Trim(txtNitrite.Text))      && !IsNumeric(txtNitrite.Text),      Set(varSaveError, "NITRITE (NO2) must be a number."));
If(IsBlank(varSaveError) && !IsBlank(Trim(txtNitrate.Text))      && !IsNumeric(txtNitrate.Text),      Set(varSaveError, "NITRATE (NO3) must be a number."));
If(IsBlank(varSaveError) && !IsBlank(Trim(txtFreeCopper1.Text))  && !IsNumeric(txtFreeCopper1.Text),  Set(varSaveError, "Free Copper 1 must be a number."));
If(IsBlank(varSaveError) && !IsBlank(Trim(txtFreeCopper2.Text))  && !IsNumeric(txtFreeCopper2.Text),  Set(varSaveError, "Free Copper 2 must be a number."));
If(IsBlank(varSaveError) && !IsBlank(Trim(txtTurbidityNTU.Text)) && !IsNumeric(txtTurbidityNTU.Text), Set(varSaveError, "Turbidity must be a number."));
If(IsBlank(varSaveError) && !IsBlank(Trim(txtSalinity.Text))     && !IsNumeric(txtSalinity.Text),     Set(varSaveError, "Salinity must be a number."));
If(IsBlank(varSaveError) && !IsBlank(Trim(txtCaHardness.Text))   && !IsNumeric(txtCaHardness.Text),   Set(varSaveError, "Ca Hardness must be a number."));
If(IsBlank(varSaveError) && !IsBlank(Trim(txtChloride.Text))     && !IsNumeric(txtChloride.Text),     Set(varSaveError, "Chloride must be a number."));
If(IsBlank(varSaveError) && !IsBlank(Trim(txtTemperature.Text))  && !IsNumeric(txtTemperature.Text),  Set(varSaveError, "Temperature must be a number."));

If(
    IsBlank(varSaveError) &&
    IsBlank(Trim(txtAlkalinity.Text)) &&
    IsBlank(Trim(txtGHardness.Text)) &&
    IsBlank(Trim(txtpH.Text)) &&
    IsBlank(Trim(txtPhosphorus.Text)) &&
    IsBlank(Trim(txtTAN.Text)) &&
    IsBlank(Trim(txtNitrite.Text)) &&
    IsBlank(Trim(txtNitrate.Text)) &&
    IsBlank(Trim(txtFreeCopper1.Text)) &&
    IsBlank(Trim(txtFreeCopper2.Text)) &&
    IsBlank(Trim(txtTurbidityNTU.Text)) &&
    IsBlank(Trim(txtSalinity.Text)) &&
    IsBlank(Trim(txtCaHardness.Text)) &&
    IsBlank(Trim(txtChloride.Text)) &&
    IsBlank(Trim(txtTemperature.Text)),
    Set(varSaveError, "Please enter at least one reading value.")
);


If(
    !IsBlank(varSaveError),
    Notify(varSaveError, NotificationType.Error),

    With(
        {
            dt: Date(
                    Year(dpReadingDate.SelectedDate),
                    Month(dpReadingDate.SelectedDate),
                    Day(dpReadingDate.SelectedDate)
                )
                + Time(
                    Value(Left(txtReadingTime.Text, 2)),
                    Value(Mid(txtReadingTime.Text, 4, 2)),
                    0
                ),
            initials: Trim(txtInitials.Text),
            notesText: Trim(txtNotes.Text)
        },

        Set(
            varSavedReading,
            Patch(
                WQ_Readings,
                Defaults(WQ_Readings),
                {
                    Title: Text(GUID()),
                    Farm: varMyFarm,
                    Site: ddSite.Selected.Site,
                    Unit: ddUnit.Selected.Unit,
                    UnitId: ddUnit.Selected.UnitId,
                    ReadingDate: dpReadingDate.SelectedDate,
                    ReadingTime: txtReadingTime.Text,
                    ReadingDateTime: dt,
                    EnteredByInitials: initials,

                    /* Parameters */
                    Alkalinity:   If(IsBlank(Trim(txtAlkalinity.Text)),   Blank(), Value(txtAlkalinity.Text)),
                    GHardness:    If(IsBlank(Trim(txtGHardness.Text)),    Blank(), Value(txtGHardness.Text)),
                    pH:           If(IsBlank(Trim(txtpH.Text)),           Blank(), Value(txtpH.Text)),
                    Phosphorus:   If(IsBlank(Trim(txtPhosphorus.Text)),   Blank(), Value(txtPhosphorus.Text)),
                    TAN:          If(IsBlank(Trim(txtTAN.Text)),          Blank(), Value(txtTAN.Text)),
                    Nitrite:      If(IsBlank(Trim(txtNitrite.Text)),      Blank(), Value(txtNitrite.Text)),
                    Nitrate:      If(IsBlank(Trim(txtNitrate.Text)),      Blank(), Value(txtNitrate.Text)),
                    FreeCopper1:  If(IsBlank(Trim(txtFreeCopper1.Text)),  Blank(), Value(txtFreeCopper1.Text)),
                    FreeCopper2:  If(IsBlank(Trim(txtFreeCopper2.Text)),  Blank(), Value(txtFreeCopper2.Text)),
                    TurbidityNTU: If(IsBlank(Trim(txtTurbidityNTU.Text)), Blank(), Value(txtTurbidityNTU.Text)),
                    Salinity:     If(IsBlank(Trim(txtSalinity.Text)),     Blank(), Value(txtSalinity.Text)),
                    CaHardness:   If(IsBlank(Trim(txtCaHardness.Text)),   Blank(), Value(txtCaHardness.Text)),
                    Chloride:     If(IsBlank(Trim(txtChloride.Text)),     Blank(), Value(txtChloride.Text)),
                    Temperature:  If(IsBlank(Trim(txtTemperature.Text)),  Blank(), Value(txtTemperature.Text)),

                    Notes: If(IsBlank(notesText), Blank(), notesText)
                }
            )
        );

        Set(varInitials, initials);
        Notify("Saved", NotificationType.Success);

        /* Refresh defaults for next entry */
        Set(varDefaultTime, Text(Now(), "HH:mm"));
        Reset(txtReadingTime);
        Reset(dpReadingDate);

        /* Clear parameter inputs + Notes */
        Reset(txtAlkalinity);
        Reset(txtGHardness);
        Reset(txtpH);
        Reset(txtPhosphorus);
        Reset(txtTAN);
        Reset(txtNitrite);
        Reset(txtNitrate);
        Reset(txtFreeCopper1);
        Reset(txtFreeCopper2);
        Reset(txtTurbidityNTU);
        Reset(txtSalinity);
        Reset(txtCaHardness);
        Reset(txtChloride);
        Reset(txtTemperature);
        Reset(txtNotes)
    )
);
